import { Button, Input } from 'antd'
import React, { useState } from 'react'

export default function About() {
const [value ,setValue] = useState('')
const [state,setState] = useState([])
const [follower,setFollower] = useState([])
//follows
const handleFollower = async()=>{
  let res = await fetch(`https://api.github.com/users/${value}/followers`)
  res = await res.json()
  setFollower([res])
}
console.log('follower', follower)
//data
  const handleChange = (e)=>{
setValue(e.target.value)
}
const handleSearch = async()=>{
  
  let response = await fetch(`https://api.github.com/users/${value}`)
  response = await response.json()
  // console.log('drr',value)    
  console.log ('ddddddddd',response)
  setState([response]);  }
  console.log('saved',state)
  return (
    <>
    <div className='second'>
      <div className='container'>
        <div className='row'>
          <div className='col d-flex justify-content-center'>
    <Input  onChange={handleChange} className='w-25' type='text' placeholder='profile name'/>
<Button className='my-2' onClick={handleSearch}>search</Button>
          </div>
        </div>
      <div className='row'>
       {state
       .map((item)=>{

   return    (  <div className='col-12 mt-5 d-flex justify-content-evenly'>
      <img src={item.avatar_url} alt='profile pic' width='220px'className='imge-fluid'/>
       <div>

       <h3><b>Name:</b>{item.login}
        <br/> <b>company:</b>{item.company}
        <br/>
        <b>bio:</b>{item.bio}
        <br/>
        <b>Created at:</b>{item.created_at}
        <br/>
        <b>UpDated at:</b>{item.updated_at}
       </h3>
  <button className='btn btn-danger' onClick={handleFollower}>See Followers</button>
       </div>
        </div>)
      })}
        </div>
      </div>
    </div>

    
    </>
  )
}
