import axios from 'axios'
import React, { useEffect, useState } from 'react'

export default function Hero() {
const [value ,setValue] = useState('')
const [data,setData] = useState('')
  const handleChange = (e)=>{
setValue(e.target.value)
 }
 //axios promise
 const fatchItems = ()=>{
 const array = []
  axios.get('https://api.github.com/users')
.then((res)=>{
const doc = res.data
array.push(doc)
setData(array)
})
}
useEffect(()=>{
  fatchItems()
},[])
 
const hendle= ()=>{
  console.log ('this is ',data)
}

return (
    <>

    
    <div className=''>
      <div className='container'>
        <div className='row'>
          <div className='col'>
            <h1 className='text-center'>GitHub User profile</h1>
            <div className='row'>
              <div className='col text-center'>
       <input type='text' placeholder='Enter profile name' onChange={handleChange}/>
             <h1>{value}</h1>
              </div>
            </div>
<button onClick={hendle}>on</button>            
            <div className='row'>
              <div className='col'>
               
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
    </>
  )
}
