import axios from 'axios'
import React, { useEffect, useState } from 'react'

export default function Hero() {
const [value ,setValue] = useState('')
const [data,setData] = useState('')
  const handleChange = (e)=>{
setValue(e.target.value)
 }
 //axios promise
 useEffect(()=>{

   axios.get('https://api.github.com/users')
   .then((res)=>{
     console.log('This is my Rest api DATA',res.data)
    //  console.log('This is my Rest',data)
    })
  },[])
 return (
    <>
    <div className=''>
      <div className='container'>
        <div className='row'>
          <div className='col'>
            <h1 className='text-center'>GitHub User profile</h1>
            <div className='row'>
              <div className='col text-center'>
       <input type='text' placeholder='Enter profile name' onChange={handleChange}/>
             <h1>{value}</h1>
              </div>
            </div>
            <div className='row'>
              <div className='col'>
                {
                  res.data.map((items)=>{
return  <>
<h1>{items.name}</h1>
</>
                  })
                }
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
    </>
  )
}
