import { Button, Input } from 'antd'
import React, { useState } from 'react'

export default function About() {
const [value ,setValue] = useState('')
const [state,setState] = useState([])
  const handleChange = (e)=>{
setValue(e.target.value)
}
const handleSearch = async()=>{
  let response = await fetch(`https://api.github.com/users/${value}`)
  response = await response.json()
  // console.log('drr',value)    
  console.log ('ddddddddd',response)
  setState([...state, response])
  }
  console.log('saved',state)
  return (
    <>
    <div className='second'>
      <div className='container'>
        <div className='row'>
          <div className='col d-flex justify-content-center'>
    <Input  onChange={handleChange} className='w-25' type='text' placeholder='profile name'/>
<Button onClick={handleSearch}>search</Button>
          </div>
        </div>
      <div className='row'>
       {state
       .map((item)=>{

   return    (  <div className='col d-flex justify-content-evenly'>
      <img src={item.avatar_url} alt='profile pic' width='250px'className='imge-fluid'/>
       <div>

       <h3>Name:{item.login}
        <br/>company:{item.company}
        <br/>
        bio:{item.bio}
        <br/>
        Created at:{item.created_at}
        <br/>
        UpDated at:{item.updated_at}
       </h3>
  <button className='btn btn-danger'>See Followers</button>
       </div>
        </div>)
      })}
        </div>
      </div>
    </div>

    
    </>
  )
}
